 .	N RM S RM=""
 .	;   #ACCEPT Date=10/24/2008; Pgm=RussellDS; CR=30801; Group=ACCESS
 .	D CASUPD^DBSEXECU("DEVICE",oldkeys,newkeys)
 .	I ER D throwError($get(RM))
